import React,{useState,useEffect} from"react"
import axios from "axios";
import { useParams } from 'react-router-dom';
import { Link } from "react-router-dom";

export default function VehiDe(){

    const{id}=useParams();

    const [vehicle, setVehicles] = useState([]);

    const getVehicle = () => {
        axios.get('http://localhost:6060/Vehicle/get/'+id)
        .then((res) => {
            console.log(res.data);
            setVehicles(res.data.Vehicle);
            
        })
        .catch((err) => {
            alert(err.message);
        });    
        
    };

    useEffect(() => {
        getVehicle();
        
    }, []);


    return(
<div>
<div className="col-md-6" style={{textAlign:"center", display: "flex", marginBottom: "26px", width:"100%"}} >
      <div className="gallery_box" style={{display: "flex", justifyContent: "center"}}>
        <div className="gallery_img" style={{marginRight: "20px",}}>
        <p className="looking_text" >{vehicle.regnumber}</p>
        </div>
      </div>
    </div>
    <div className="col-md-6" style={{ display: "flex", marginBottom: "26px",marginLeft:"26%" , width:"100%"}}>
      <div className="gallery_box" style={{display: "flex"}}>
        <div className="gallery_img" style={{marginRight: "20px",}}>
          <img src={`http://localhost:6060/images/${vehicle.image}`} alt="Vehicle" />
        </div>
      </div>
    </div>

    <div className="col-md-6" style={{display: "flex", marginBottom: "26px"}}>
      <div className="gallery_box" style={{display: "flex"}}>
        <div className="details_container" >
          <h3 className="types_text" >{vehicle.model}</h3>
          <p className="looking_text" >Start per day: {vehicle.priceperday}</p>
          {/* Additional details */}
          <ul className="vehicle_details" >
            <li><strong>Brand:</strong> {vehicle.brand}</li>
            <li><strong>Year:</strong> {vehicle.year}</li>
            <li><strong>Type:</strong> {vehicle.type}</li>
            <li><strong>Fuel Type:</strong> {vehicle.fueltype}</li>
            <li><strong>Available:</strong> {vehicle.available}</li>
            <li><strong>Reg Number:</strong> {vehicle.regnumber}</li>
            {/* Include other details similarly */}
          </ul>
          <div className="read_bt" >
            <Link to="#">Book Now</Link>
          </div>
        </div>
      </div>
    </div>
</div>
    )
}
