import React,{useState,useEffect} from"react"
import axios from "axios";
import { useParams } from 'react-router-dom';
import { Link } from "react-router-dom";

export default function VehiDe(){

    const{id}=useParams();

    const [vehicle, setVehicles] = useState([]);

    const getVehicle = () => {
        axios.get('http://localhost:6060/Vehicle/get/'+id)
        .then((res) => {
            console.log(res.data);
            setVehicles(res.data.Vehicle);
            
        })
        .catch((err) => {
            alert(err.message);
        });    
        
    };

    useEffect(() => {
        getVehicle();
        
    }, []);


    return (
        <div className="dii">
  <div className="vehicle-details-container1">
    {/* Vehicle Details */}
  </div>
  <div className="veh">
    <div>
      <img className="img" style={{ width: "auto" }} src={`http://localhost:6060/images/${vehicle.image}`} alt="Vehicle" />
    </div>
  </div>
  <div style={{ display: "flex", justifyContent: "center", marginTop: "10%" }}>
    <form className="booking-form" style={{ width: "40%", border: "2px solid #ccc", borderRadius: "50px", padding: "20px" }}>
      <div className="form-row">
        <div className="form-group">
          <label className="lbname" htmlFor="pickupDate">Pickup Date:</label>
          <input style={{ background: "white" }} className="form-control" type="date" id="pickupDate" name="pickupDate" required />
        </div>
        <div className="form-group">
          <label className="lbname" htmlFor="returnDate">Return Date:</label>
          <input style={{ background: "white" }} className="form-control" type="date" id="returnDate" name="returnDate" required />
        </div>
      </div>
      <div className="form-row">
        <div className="form-group">
          <label className="lbname" htmlFor="contactNumber">Contact Number:</label>
          <input style={{ background: "white" }} className="form-control" type="tel" id="contactNumber" name="contactNumber" required />
        </div>
        <div className="form-group">
          <label className="lbname" htmlFor="driverLicenseNumber">Driver's License Number:</label>
          <input style={{ background: "white" }} className="form-control" type="text" id="driverLicenseNumber" name="driverLicenseNumber" required />
        </div>
      </div>
      <div className="form-group" style={{ textAlign: "center" }}>
        <label className="lbname" htmlFor="additionalRequirement">Additional Requirements:</label>
        <textarea style={{ background: "white" }} className="form-control" id="additionalRequirement" name="additionalRequirement" rows="4"></textarea>
      </div>
      <div className="form-group" style={{ textAlign: "center" }}>
        <button className="btn btn-primary mb-3" type="submit">Book Car</button>
      </div>
    </form>
  </div>
</div>

      );
      
}
