import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Header from './Components/Header';
import AddVehicle from './Components/AddVehicle';
import AllVehicle from './Components/AllVehicle';
import UpdateVehicle from './Components/UpdateVehicle';
import VehicleDetail from "./Components/VehicleDetail";
import HomePage from "./Components/HomePage";
import LoginHandle from "./Components/LoginPage";
import { AuthService } from "./Components/AuthService ";

import './App.css';
import './css/bootstrap.min.css';
import './css/style.css';
import './css/side.css';
import './css/vehicle.css';

function App() {
  const isLoggedIn = AuthService.isLoggedIn(); // Check if user is logged in

  return (
    <Router>
      <div className="App">
        {isLoggedIn ? <Header /> : <LoginHandle />} {/* Render Header only if user is logged in */}
        <Routes>
          <Route
            path="/login"
            element={isLoggedIn ? <Navigate to="/Header/:id" /> : <LoginHandle />}
          />
          <Route
            path="/ho"
            element={isLoggedIn ? <HomePage /> : <Navigate to="/login" />}
          />
          <Route
            path="/Header/:id"
            element={isLoggedIn ? <Header /> : <Navigate to="/login" />}
          />
          <Route
            path="/add"
            element={isLoggedIn ? <AddVehicle /> : <Navigate to="/login" />}
          />
          <Route
            path="/all"
            element={isLoggedIn ? <AllVehicle /> : <Navigate to="/login" />}
          />
          <Route
            path="/update/:id"
            element={isLoggedIn ? <UpdateVehicle /> : <Navigate to="/login" />}
          />
          <Route
            path="/get/:id"
            element={isLoggedIn ? <VehicleDetail /> : <Navigate to="/login" />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
