import React,{useState,useEffect} from"react"
import axios from "axios";
import { useParams } from 'react-router-dom';
import { Link } from "react-router-dom";

export default function VehiDe(){

    const{id}=useParams();

    const [vehicle, setVehicles] = useState([]);

    const getVehicle = () => {
        axios.get('http://localhost:6060/Vehicle/get/'+id)
        .then((res) => {
            console.log(res.data);
            setVehicles(res.data.Vehicle);
            
        })
        .catch((err) => {
            alert(err.message);
        });    
        
    };

    useEffect(() => {
        getVehicle();
        
    }, []);


    return(
        <div>
      <div style={{ background: "#fe5b29", width: "50%", height: "90vh", borderBottomRightRadius: "2000px", display: "flex", flexWrap: "wrap", alignItems: "center", justifyContent: "center" }}>
        <div style={{ width: "30%", margin: "10px", color: "whitesmoke", fontSize: "20px" }}>
          <p style={{ paddingTop: "30px", fontSize: "36px" }}>{vehicle.regnumber}</p>
        </div>
        <div style={{ width: "30%", margin: "10px", color: "whitesmoke", fontSize: "20px" }}>
          <p style={{ paddingTop: "20px" }}><strong>Model:</strong> {vehicle.model}</p>
          <p style={{ paddingTop: "20px" }}><strong>Brand:</strong> {vehicle.brand}</p>
          <p style={{ paddingTop: "20px" }}><strong>Year:</strong> {vehicle.year}</p>
        </div>
        <div style={{ width: "30%", margin: "10px", color: "whitesmoke", fontSize: "20px" }}>
          <p style={{ paddingTop: "20px" }}><strong>Type:</strong> {vehicle.type}</p>
          <p style={{ paddingTop: "20px" }}><strong>Fuel Type:</strong> {vehicle.fueltype}</p>
          <p style={{ paddingTop: "20px" }}><strong>Available:</strong> {vehicle.available}</p>
        </div>
        <div style={{ width: "30%", margin: "10px", color: "whitesmoke", fontSize: "20px" }}>
          <p style={{ paddingTop: "20px" }}><strong>Reg Number:</strong> {vehicle.regnumber}</p>
          <p style={{ paddingTop: "20px" }}>Per day Rs: {vehicle.priceperday}.00</p>
          {/* Include other details similarly */}
        </div>
        {/* Repeat this structure for additional vehicle components */}
      </div>
    </div>
    )
}
