import React,{useState,useEffect} from"react"
import axios from "axios";
import { useParams } from 'react-router-dom';
import { Link } from "react-router-dom";

export default function VehiDe(){

    const{id}=useParams();

    const [vehicle, setVehicles] = useState([]);

    const getVehicle = () => {
        axios.get('http://localhost:6060/Vehicle/get/'+id)
        .then((res) => {
            console.log(res.data);
            setVehicles(res.data.Vehicle);
            
        })
        .catch((err) => {
            alert(err.message);
        });    
        
    };

    useEffect(() => {
        getVehicle();
        
    }, []);


    return(


                
                    <div class="col-md-4" style={{ marginBottom: "26px" }}>
                    <div class="gallery_box">
                        <div class="gallery_img">
                        <img src={`http://localhost:6060/images/${vehicle.image}`} alt="Vehicle" />
                        </div>
                        <h3 class="types_text">{vehicle.model}</h3>
                        <p class="looking_text">Start per day: {vehicle.priceperday}</p>
                        {/* Additional details */}
                        <ul class="vehicle_details">
                        <li>Brand:{vehicle.brand}</li>
                        <li>Year: {vehicle.year}</li>
                        <li>Type: {vehicle.type}</li>
                        <li>Fuel Type: {vehicle.fueltype}</li>
                        <li>Available: {vehicle.available}</li>
                        <li>regnumber: {vehicle.regnumber}</li>
                        {/* Include other details similarly */}
                        </ul>
                        <div class="read_bt">
                        <Link to="#">Book Now</Link>
                        </div>
                    </div>
                    </div>
       



    )
}
