import { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './Components/Header';
import AddVehicle from './Components/AddVehicle';
import AllVehicle from './Components/AllVehicle';
import UpdateVehicle from './Components/UpdateVehicle';
import VehicleDetail from "./Components/VehicleDetail";
import dashbo from "./Components/dashbo";
import HomePage from "./Components/HomePage";
import LoginHandle from "./Components/LoginPage";

import './App.css';
import './css/bootstrap.min.css';
import './css/style.css';
import './css/side.css';
import './css/vehicle.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to handle successful login
  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
  };

  return (
    <Router>
      <div className="App">
        {!isLoggedIn ? ( // Render LoginHandle if not logged in
          <LoginHandle onLoginSuccess={handleLoginSuccess} />
        ) : (
          <Routes>
            <Route path="/ho" element={<HomePage />} />
            <Route path="/Header/:id" element={<Header />} />
            <Route path="/add" element={<AddVehicle />} />
            <Route path="/all" element={<AllVehicle />} />
            <Route path="/update/:id" element={<UpdateVehicle />} />
            <Route path="/get/:id" element={<VehicleDetail />} />
            {/* Other routes */}
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
