import React,{useState,useEffect} from"react"
import axios from "axios";
import { useParams } from 'react-router-dom';
import { Link } from "react-router-dom";

export default function VehiDe(){

    const{id}=useParams();

    const [vehicle, setVehicles] = useState([]);

    const getVehicle = () => {
        axios.get('http://localhost:6060/Vehicle/get/'+id)
        .then((res) => {
            console.log(res.data);
            setVehicles(res.data.Vehicle);
            
        })
        .catch((err) => {
            alert(err.message);
        });    
        
    };

    useEffect(() => {
        getVehicle();
        
    }, []);


    return(
        <div >
            <div style={{background:"#fe5b29" ,width:"25%" ,height:"90vh",borderBottomRightRadius:"2000px",borderBottomLeftRadius:"2000px"}}>
            <ul className="vehicle_details">
            <li style={{marginTop:"30px",color:"whitesmoke",fontSize:"36px"}}>{vehicle.regnumber}</li>
            <li><strong>Model:</strong>{vehicle.model}</li>
            <li ><strong>Brand:</strong> {vehicle.brand}</li>
            <li ><strong>Year:</strong> {vehicle.year}</li>
            <li ><strong>Type:</strong> {vehicle.type}</li>
            <li ><strong>Fuel Type:</strong> {vehicle.fueltype}</li>
            <li ><strong>Available:</strong> {vehicle.available}</li>
            <li ><strong>Reg Number:</strong> {vehicle.regnumber}</li>
            <li className="price-per-day">Per day Rs: {vehicle.priceperday}.00</li>
            {/* Include other details similarly */}
          </ul>

            </div>
        </div>
    )
}
