import React,{useState,useEffect} from"react"
import axios from "axios";
import { useParams } from 'react-router-dom';
import { Link } from "react-router-dom";
import { AuthService } from "./AuthService ";

export default function VehiDe(){

    const{id}=useParams();
    const{uId}=useParams();

    const [vehicle, setVehicles] = useState([]);
    const [user, setUser] = useState([]);

    //get user details
    const getUser = () => {
      axios.get(`http://localhost:6060/User/getid/${uId}`)
          .then((res) => {
            console.log(uId);
              const userArray = res.data.user;
              if (userArray.length > 0) {
                  const userData = userArray[0];
                  console.log("User Data:", userData);
                  setUser(userData);
              } else {
                  console.log("User not found");
              }
          })
          .catch((err) => {
              console.error("Error fetching user:", err);
              alert("Error fetching user: " + err.message);
          });
  };

//get vehicle delails
    const getVehicle = () => {
        axios.get('http://localhost:6060/Vehicle/get/'+id)
        .then((res) => {
            console.log(res.data);
            setVehicles(res.data.Vehicle);
            
        })
        .catch((err) => {
            alert(err.message);
        });       
    };


    useEffect(() => {
        getVehicle();
        
    }, []);
    useEffect(() => {
     
      getUser();
   }, []);

    const handleLogout = () => {
      AuthService.logout(); // Call the logout method from AuthService
      window.location.href = '/'; // Redirect to the  page after logout
     };
      // Correct usage of AuthService
      const isLoggedIn = AuthService.isLoggedIn();

      useEffect(() => {
        if (!isLoggedIn) {
        // Redirect to  if user is not logged in
        window.location.href = '/';
        }
      }, [isLoggedIn]);

      const [txtPick, setPick] = useState("");
      const [txtRetu, setRetu] = useState("");
      const [txtCon, setCon] = useState("");
      const [txtAdRe, setAdRe] = useState("");
      const [txtDLicen, setDLicen] = useState("");
      const txtRegnumber=vehicle.regnumber;
      const txtUid=uId;
      const txtEmail=user.email;


      function sendData(e){
        e.preventDefault();

        const formData=new FormData();
      formData.append('pickupdate', txtPick);
      formData.append('returndate', txtRetu);
      formData.append('regnumber', txtRegnumber);
      formData.append('uid', txtUid);
      formData.append('email', txtEmail);
      formData.append('contactno', txtCon);
      formData.append('dlicen', txtDLicen);
      formData.append('aditionalreq', txtAdRe);
  
      console.log(formData);
  
      axios.post("http://localhost:6060/Booking/add", formData)
        .then(() => {
          alert("Booking added successfully");
          
        })
