import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

import axios from 'axios';
import deleteVehicle from './DeleteVehicle';

export default function AllVehicle() {
  const [vehicle, setVehicles] = useState([]);

  const getVehicle = () => {
    axios.get('http://localhost:6060/Vehicle/')
      .then((res) => {
        console.log(res.data);
        setVehicles(res.data);
      })
      .catch((err) => {
        alert(err.message);
      });    
      
  };

  

  useEffect(() => {
    getVehicle();
    
  }, []);

  const handleDelete = (vehicleId) => {
    deleteVehicle(vehicleId)
      .then(() => {
        alert('Vehicle deleted successfully');
        getVehicle();
      })
      .catch((err) => {
        alert('Vehicle not deleted: ' + err);
      });
  };

  

  return (
    <div>
      <h1>All Vehicles</h1>
    <div style={{ height: '400px', overflowY: 'auto' }}>
     
      <table style={{ borderCollapse: 'collapse', width: '90%', margin: 'auto' }}>
        <thead style={{ position: 'sticky', top: '0', zIndex: '1', backgroundColor: '#fff' }}>
          <tr style={{ borderBottom: '1px solid #000' }}>
            <th>regnumber</th>
            <th>Brand</th>
            <th>Model</th>
            <th>available</th>
            <th>Price Per Day</th>
            <th colSpan={3}>Action</th>
            
          </tr>
        </thead>
        <tbody>
          {vehicle.map((v, index) => (
            <tr key={index} style={{ borderBottom: '1px solid #000' }}>
              <td>{v.regnumber}</td>
              <td>{v.brand}</td>
              <td>{v.model}</td>
              <td>{v.available}</td>
              <td>Rs: {v.priceperday}</td>
              <td>
                <img
                  src={`http://localhost:6060/images/eye.png`}
                  alt={`${index}`}
                  style={{ width: '40px', height: '40px', objectFit: 'cover', cursor: 'pointer', marginRight: '10px' }}
                  onClick={() => handleDelete(v._id)}
                />
               </td>
               <td> 
                <img
                  src={`http://localhost:6060/images/delete.png`}
                  alt={`${index}`}
                  style={{ width: '30px', height: '30px', objectFit: 'cover', cursor: 'pointer' }}
                  onClick={() => handleDelete(v._id)}
                />
              </td>
              <td>
                <a className="nav-link" href={`/update/${v._id}`}><img
                  src={`http://localhost:6060/images/edit.png`}
                  alt={`${index}`}
                  style={{ width: '30px', height: '30px', objectFit: 'cover', cursor: 'pointer', marginRight: '10px' }}
                /></a>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </div>
  );
}
