import React,{useState,useEffect} from"react"
import axios from "axios";
import { useParams } from 'react-router-dom';
import { Link } from "react-router-dom";

export default function VehiDe(){

    const{id}=useParams();

    const [vehicle, setVehicles] = useState([]);

    const getVehicle = () => {
        axios.get('http://localhost:6060/Vehicle/get/'+id)
        .then((res) => {
            console.log(res.data);
            setVehicles(res.data.Vehicle);
            
        })
        .catch((err) => {
            alert(err.message);
        });    
        
    };

    useEffect(() => {
        getVehicle();
        
    }, []);


    return(
        <div className="vehicle-details-container">
      <div className="vehicle-details-section">
        <div className="gallery_box image-section">
          <p className="reg-number">{vehicle.regnumber}</p>
        </div>
      </div>
      <div className="vehicle-details-section">
        <div className="gallery_box details-section">
          <ul className="vehicle_details">
            <li className="model">{vehicle.model}</li>
            <li className="detail"><strong>Brand:</strong> {vehicle.brand}</li>
            <li className="detail"><strong>Year:</strong> {vehicle.year}</li>
            <li className="detail"><strong>Type:</strong> {vehicle.type}</li>
            <li className="detail"><strong>Fuel Type:</strong> {vehicle.fueltype}</li>
            <li className="detail"><strong>Available:</strong> {vehicle.available}</li>
            <li className="detail"><strong>Reg Number:</strong> {vehicle.regnumber}</li>
            <li className="price-per-day">Per day Rs: {vehicle.priceperday}.00</li>
            {/* Include other details similarly */}
          </ul>
        </div>
        <div className="gallery_box image-section">
          <img src={`http://localhost:6060/images/${vehicle.image}`} alt="Vehicle" />
        </div>
      </div>
    </div>
    )
}
