import React,{useState} from"react"
import axios from "axios";


export default function AddVehicle() {
    const [txtBrand, setBrand] = useState("");
    const [txtModel, setModel] = useState("");
    const [txtType, setType] = useState("");
    const [txtYear, setYear] = useState("");
    const [txtFeatures, setFeatures] = useState("");
    const [txtFuelType, setFuelType] = useState("");
    const [txtPricePerDay, setPricePerDay] = useState("");
    const [txtReg,setReg]=useState("");
    const [txtTransmission,setTrans]=useState("");
    const [txtAvai,setAvai]=useState("");

    const [fileImage, setImage] = useState(); // Changed to null
  
    function sendData(e) {
      e.preventDefault();
  
      if (!txtBrand || !txtModel || !txtType || !txtYear || !txtFeatures || !txtFuelType || !txtPricePerDay || !fileImage) {
        alert("Please fill in all fields and select an image.");
        return;
      }
      
      const formData = new FormData();
      formData.append('image', fileImage); // Updated
  
      formData.append('brand', txtBrand);
      formData.append('model', txtModel);
      formData.append('year', txtYear);
      formData.append('type', txtType);
      formData.append('fueltype', txtFuelType);
      formData.append('features', txtFeatures);
      formData.append('priceperday', txtPricePerDay);
      formData.append('transmission', txtTransmission);
      formData.append('regnumber', txtReg);
      formData.append('available', txtAvai);
  
      console.log(formData);
  
      axios.post("http://localhost:6060/Vehicle/add", formData)
        .then(() => {
          alert("Vehicle added successfully");
          
        })
        .catch((err) => {
          alert("Vehicle not added: " + err);
        
        });
    }
   
    return(
        <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '850px', margin: '0 auto' }}>
            <h1>Add Vehicle</h1>
        <form onSubmit={sendData} class="row g-4">
            <div>
            {/* Brand, Model, and Year */}
            <div style={{ display: 'flex', gap: '30px', marginBottom: '30px' }}>
                <div>
                <label>Brand: </label>
                <select class="form-control" name="Brand" id="txtBrand"  onChange={(e) => {
                    setBrand(e.target.value);
                }}>
                    <option value="volvo">Volvo</option>
                    <option value="saab">Saab</option>
                    <option value="mercedes">Mercedes</option>
                    <option value="audi">Audi</option>
                </select>
                </div>
                <div>
                <label>Model:</label>
                <input type="text" class="form-control" id="txtModel"  onChange={(e) => {
                    setModel(e.target.value);
                }} />
                </div>
                <div>
                <label>Year:</label>
                <input type="text" class="form-control"  id="txtYear" onChange={(e) => {
                    setYear(e.target.value);
                }} />
                </div>
            </div>

            {/* Type, Transmission, and Availability */}
            <div style={{ display: 'flex', gap: '30px', marginBottom: '30px' }}>
                <div>
                <label>Type:</label>
                <select name="Type" class="form-control"  id="txtType" onChange={(e) => {
                    setType(e.target.value);
                }}>
                    <option value="suv">SUV</option>
                    <option value="sport">Sport</option>
                    <option value="luxury">Luxury</option>
                </select>
                </div>
                <div>
                <label>Transmission:</label>
                <select name="Transmission" class="form-control"  id="txtTransmission" onChange={(e) => {
                    setTrans(e.target.value);
                }}>
                    <option value="Auto">Auto</option>
                    <option value="Manual">Manual</option>
                </select>
                </div>
                <div>
                <label>Availability:</label>
                <select name="Availability" class="form-control"  id="txtAvai" onChange={(e) => {
                    setAvai(e.target.value);
                }}>
                    <option value="rent">Rent</option>
                    <option value="free">Free</option>
                </select>
                </div>
            </div>

            {/* Fuel Type, Seats, and Price Per Day */}
            <div style={{ display: 'flex', gap: '30px', marginBottom: '30px' }}>
                <div>
                <label>Fuel Type:</label>
                <select name="FuelType" class="form-control"  id="txtFuelType"  onChange={(e) => {
                    setFuelType(e.target.value);
                }}>
                    <option value="petrol">Petrol</option>
                    <option value="diesel">Diesel</option>
                    <option value="electric">Electric</option>
                </select>
                </div>
                <div>
                <label>Seats:</label>
                <input type="text" class="form-control"  id="txtFeatures"  onChange={(e) => {
                    setFeatures(e.target.value);
                }} />
                </div>
                <div>
                <label>Price Per Day:</label>
                <input type="text" class="form-control"  id="txtPricePerDay"  onChange={(e) => {
                    setPricePerDay(e.target.value);
                }} />
                </div>
            </div>

            {/* Image upload */}

            <div style={{ display: 'flex', gap: '30px', marginBottom: '30px' }}>
            <div>
                <label>Reg-No:</label>
                <input type="text" class="form-control"  id="txtReg"  onChange={(e) => {
                    setReg(e.target.value);
                }} />
                </div>
                <div>
                <label>Image:</label>
                <input type="file" class="form-control"  id="fileImage" onChange={(e) => {
                    const selectedFile = e.target.files[0];
                    setImage(selectedFile);
                }} />
                </div>
            </div>

            {/* Submit button */}
            <div class="col-auto">
                <button type="submit" class="btn btn-primary mb-3" id="submit">Submit</button>
            </div>
            </div>
        </form>
        </div>

    )
}
