import React,{useState,useEffect} from"react"
import axios from "axios";
import { useParams } from 'react-router-dom';

export default function VehicleDetail(){

    const{id}=useParams();

    const [vehicle, setVehicles] = useState([]);

    const getVehicle = () => {
        axios.get('http://localhost:6060/Vehicle/get/'+id)
        .then((res) => {
            console.log(res.data);
            setVehicles(res.data.Vehicle);
            
        })
        .catch((err) => {
            alert(err.message);
        });    
        
    };

    useEffect(() => {
        getVehicle();
        
    }, []);

    return(

        <div>
                <div class="vehicle-cards">
                <div class="vehicle-card"> 
                    <div class="vehicle-details">
                    <h3 style={{color:"black"}}>{vehicle.regnumber}</h3>   
                    <p>Brand:<span>{vehicle.brand}</span></p>
                    <p>Model:<span>{vehicle.model}</span></p>
                    <p>Year:<span>{vehicle.year}</span></p>
                    <p>Type:<span>{vehicle.type}</span></p>
                    <p>Fuel:<span>{vehicle.fueltype}</span></p>
                    <p>Price/Day:<span>{vehicle.priceperday}</span></p>
                    <p>Transmission:<span>{vehicle.regnumber}</span></p>
                    <p>Available:<span>{vehicle.available}</span></p>
                    <p>{id}</p>
                    </div>
                </div>
                
                </div>
                
                <div className="vehicle-image">
                <img src={`http://localhost:6060/images/${vehicle.image}`} alt="Vehicle" />
                </div>
                   



        </div>
    )
}
